import { useState } from "react"
import Button from "../UI/Button"
import Card from "../UI/Card"
import ErrorModal from "../UI/ErrorModal"
import classes from "./UserList.module.css"

const UserList =(props)=>{

    const [stop , setStop] = useState(null)
    const [data, setDate] = useState([])


    const stopChangeHandler = (event)=>{
         setStop(props.users.filter((user)=> user.id !=event.target.id))
        setDate ({
            title:'Удаление пользователя',
            messega:'Вы действительно хотите удалить?'
        })
    }
    const deleteHandler = (event)=>{
          if(event.target.type==='button'){
              props.onGetData(data)
              setStop(null)
          }
    }
    const cancelHandler=(event)=>{
         if(event.target.type ==='button'){
             setStop(null)
         }
    }





    return(
        <>
        {stop && (<ErrorModal  title={stop.title} messega={stop.messega} onConfirm={deleteHandler} >
               <Button onClick={cancelHandler}>cancel</Button>
            </ErrorModal>)}
        <Card className={classes.users}>
        <ul>
            {props.users.map((user)=>(
             <li key={user.id}
             name={user.name} age>
                <Button onClick={stopChangeHandler}>delete</Button>
                  ({user.age} yaers old)
             </li>
            )
            )}
        </ul>
    </Card>
    </>
    )
    

}
export default UserList
